INSERT INTO `ug_theory_copy_04_06_2015` VALUES ('1244', 'f1', 'Шиберная задвижка.', '512,675,533,701', 'Шиберная ручная задвижка  4 1/16”, 10000 Psi RTJ (коренная), среда – кислый газ. $$$Коренная задвижка пломбируется в полностью открытом положении представителем службы эксплуатации. Снятие пломбы и использование коренной задвижки возможно только по письменному разрешению Газопромыслового управления. Работа с использованием коренной задвижки допускается только в аварийных ситуациях, при этом составляется соответствующий акт.$$$', '', '1_OUS.jpg', '[a href=\"/docs/instrukciya_fa_Anson.tif\" target=_blank]Инструкция ФА Anson[/a];$$$[a href=\"/docs/instrukciya_fa_Barber.tif\" target=_blank]Инструкция ФА Barber[/a];$$$[a href=\"/docs/instrukciya_fa_Cameron.tif\" target=_blank]Инструкция ФА Cameron[/a];$$$[a href=\"/docs/instrukciya_fa_Malbranque.tif\" target=_blank]Инструкция ФА Malbrabque[/a];$$$[a href=\"/docs/instrukciya_fa_McEvoy.tif\" target=_blank]Инструкция ФА McEvoy[/a];$$$[a href=\"/docs/instrukciya_fa_zelenodoljsk.tif\" target=_blank]Инструкция ФА Зеленодольск[/a];$$$', '[a href=\"/video/Shiber.avi\" target=_blank]Шиберная задвижка[/a];$$$', '');
