INSERT INTO `ug_theory_copy_04_06_2015` VALUES ('416', 'f1', 'Манометр технический.', '504,518,541,552', 'Технический манометр, измеряющий давление газожидкостной смеси на устье скважины (до прямоточного дросселирующего клапана № 16).$$$Предельно допустимое рабочее давление Рраб. - 40 МПа.Рекомендованный верхний предел измерительной шкалы манометра - 70 МПа (допускается 100 МПа).', '', '1_OUS.jpg', '[a href=\"/docs/tablica_znacheniy_i_predelov_izmereniy.tif\" target=_blank]Таблица значений и пределов измерений[/a];$$$[a href=\"/docs/instrukciya_po_ekspluatacii_i_tehnicheskomu_obsluzhivanie_manometrov.tif\" target=_blank]Инструкция по эксплуатации манометров[/a];$$$', '', '');
