INSERT INTO `ug_theory_copy_04_06_2015` VALUES ('434', 'f2', 'Манометр технический.', '1204,344,1244,384', 'Технический манометр, измеряющий давление в трубопроводе очищенного газа (линия топливного газа на пилотную горелку подогревателя) .$$$Для подогревателя ГП1935: $$$Предельно допустимое рабочее давление $$$Р раб.= 0,07МПа.Рекомендованный верхний предел измерительной шкалы манометра - 0,16 МПа $$$Для подогревателя Natco: $$$Предельно допустимое рабочее давление $$$Р раб.= 0,035МПа.Рекомендованный верхний предел измерительной шкалы манометра - 0,1 МПа $$$Для подогревателя Волгоград-нефтемаш: $$$Предельно допустимое рабочее давление $$$Р раб.= 0,1МПа.Рекомендованный верхний предел измерительной шкалы манометра - 0,25 МПа (Допускается 0,16 МПа) ', '', '2_Podogrevatel.jpg', '[a href=\"/docs/tablica_znacheniy_i_predelov_izmereniy.tif\" target=_blank]Таблица значений и пределов измерений[/a];$$$[a href=\"/docs/instrukciya_po_ekspluatacii_i_tehnicheskomu_obsluzhivanie_manometrov.tif\" target=_blank]Инструкция по эксплуатации и обслуживанию манометров[/a];$$$', '', '');
INSERT INTO `ug_theory_copy_04_06_2015` VALUES ('436', 'f2', 'Манометр технический.', '1134,234,1176,266', 'Технический манометр, измеряющий давление в трубопроводе очищенного газа (линия топливного газа на основную  горелку подогревателя).$$$Для подогревателя ГП1935: $$$Предельно допустимое рабочее давление $$$Р раб.= 0,3МПа.Рекомендованный верхний предел измерительной шкалы манометра - 0,6 МПа $$$Для подогревателя Natco: $$$Предельно допустимое рабочее давление $$$Р раб.= 0,083МПа.Рекомендованный верхний предел измерительной шкалы манометра - 0,16 МПа $$$Для подогревателя Волгоград-нефтемаш: $$$Предельно допустимое рабочее давление $$$Р раб.= 0,25МПа.Рекомендованный верхний предел измерительной шкалы манометра - 0,4 МПа (Допускается 0,6 МПа)$$$', '', '2_Podogrevatel.jpg', '[a href=\"/docs/tablica_znacheniy_i_predelov_izmereniy.tif\" target=_blank]Таблица значений и пределов измерений[/a];$$$[a href=\"/docs/instrukciya_po_ekspluatacii_i_tehnicheskomu_obsluzhivanie_manometrov.tif\" target=_blank]Инструкция по эксплуатации и обслуживанию манометров[/a];$$$', '', '');
