INSERT INTO `ug_theory_copy_04_06_2015` VALUES ('890', 'f2', 'Датчик  давления', '462,623,501,658', 'Интеллектуальный датчик  давления, рабочий диапазон: от 0 до 16 МПа. При превышении давления выше 11,5 МПа происходит сигнализация в АСУ ТП.', '', '2_Podogrevatel.jpg', '', '', '');
