INSERT INTO `ug_theory_copy_04_06_2015` VALUES ('1324', 'f2', 'Предохранительный клапан', '1108,383,1155,420', 'Предохранительный клапан (ППК) предназначен для предохранения трубопровода очищенного газа на горелки подогревателя и приборы КИПиА от превышения давления. При достижении давления 3,5 МПа происходит автоматический сброс избыточного давления в атмосферу.', '', '2_Podogrevatel.jpg', '[a href=\"/docs/instrukciya_ppk_ap_27_dg.tif\" target=_blank]Инструкция по эксплуатации ППК АП-27 ДГ[/a];$$$[a href=\"/docs/instrukciya_ppk_arzil.tif\" target=_blank]Инструкция по эксплуатации ППК Арзил[/a];$$$[a href=\"/docs/instrukciya_ppk_blagoveshenskiy_zavod.tif\" target=_blank]Инструкция по эксплуатации ППК Благовещенский завод[/a];$$$', '[a href=\"/video/PPK.avi\" target=_blank]Предохранительный клапан[/a];$$$', '');
