INSERT INTO `ug_theory_copy_04_06_2015` VALUES ('1394', 'f5', 'Контрольный сепаратор', '660,472,1286,644', 'Контрольный сепаратор предназначен для проведения газодинамических и газоконденсатных исследований эксплуатационных скважин АГКМ с целью оценки продукции по содержанию воды,конденсата, собственного газа и общего дебита.', '', '5_KS.jpg', '[a href=\"/docs/instrukciya_kontroljnyy_separator.tif\" target=_blank]Инструкция по эксплуатации и обслуживанию контрольного сепаратора[/a];$$$', '[a href=\"/video/Separator_kontrolnii.avi\" target=_blank]Контрольный сепаратор[/a];$$$', '');
INSERT INTO `ug_theory_copy_04_06_2015` VALUES ('1396', 'f6', 'Дренажная ёмкость', '157,824,456,908', 'Дренажной емкость,которая связана с факельной системой и в которую по дренажным трубопроводам при предремонтном освобождении технологических трубопроводов сбрасывается накопившаяся в них жидкость. Жидкость из дренажной ёмкости вытесняется очищенным газом в факельный сепаратор, а оттуда насосами откачивается в газоконденсатопровод.', '', '6_FS_DE.jpg', '[a href=\"/docs/instrukciya_po_ekspluatacii_i_sosudov_rabotaushih_pod_davleniem.tif\" target=_blank]Инструкция по эксплуатации сосудов, работающих под давлением.[/a];$$$', '', '');
INSERT INTO `ug_theory_copy_04_06_2015` VALUES ('1398', 'f6', 'Факельный сепаратор', '745,230,1118,354', 'Факельный сепаратор предназначен для приёма жидкости из дренажной ёмкости УППГ и перекачки в ГКП с помощью насосов, а также для отделения жидкости от газов при сбросе на факел УПП.', '', '6_FS_DE.jpg', '[a href=\"/docs/instrukciya_fakeljnyy_separator.tif\" target=_blank]Инструкция по эксплуатации факельного сепаратора[/a];$$$', '', '');
INSERT INTO `ug_theory_copy_04_06_2015` VALUES ('1400', 'f6', 'Подпорный насос', '918,610,974,660', 'Подпорный (центробежный) насос факельного сепаратора. Предназначен для создания давления откачивающейся жидкости из факельного сепаратора на входе в дожимной насос. Производительность 5,2 м3/час, Рраб. = 0,12 МПа. Среда - конденсат, пластовая вода.', '', '6_FS_DE.jpg', '', '', '');
INSERT INTO `ug_theory_copy_04_06_2015` VALUES ('1402', 'f6', 'Подпорный насос', '918,786,976,832', 'Подпорный (центробежный) насос факельного сепаратора. Предназначен для создания давления откачивающейся жидкости из факельного сепаратора на входе в дожимной насос. Производительность 5,2 м3/час, Рраб. = 0,12 МПа. Среда - конденсат, пластовая вода.', '', '6_FS_DE.jpg', '', '', '');
INSERT INTO `ug_theory_copy_04_06_2015` VALUES ('1404', 'f6', 'Подпорный насос', '918,786,976,832', 'Подпорный (центробежный) насос факельного сепаратора. Предназначен для создания давления откачивающейся жидкости из факельного сепаратора на входе в дожимной насос. Производительность 5,2 м3/час, Рраб. = 0,12 МПа. Среда - конденсат, пластовая вода.', '', '6_FS_DE.jpg', '', '', '');
INSERT INTO `ug_theory_copy_04_06_2015` VALUES ('1406', 'f5', 'Предохранительный клапан', '156,382,238,440', 'Предохранительный клапан (ППК) предназначен для предохранения газоконденсатопровода возможного превышения рабочего давления. При превышении давления выше 11,2 МПа происходит сброс избыточного давления на вертикальное факельное устройство УППГ.', '', '5_KS.jpg', '[a href=\"/docs/instrukciya_ppk_Dresser_Velvs.tif\" target=_blank]Инструкция по эксплуатации ППК Dresser Velvs[/a];$$$[a href=\"/docs/instrukciya_ppk_Sempell.tif\" target=_blank]Инструкция по эксплуатации ППК Sempell[/a];$$$[a href=\"/docs/instrukciya_ppk_SNRI.tif\" target=_blank]Инструкция по эксплуатации ППК SNRI[/a];$$$', '[a href=\"/video/PPK.avi\" target=_blank]Предохранительный клапан[/a];$$$', '');
INSERT INTO `ug_theory_copy_04_06_2015` VALUES ('1408', 'f5', 'Предохранительный клапан', '290,471,382,519', 'Предохранительный клапан (ППК) предназначен для предохранения газоконденсатопровода возможного превышения рабочего давления. При превышении давления выше 11,2 МПа происходит сброс избыточного давления на вертикальное факельное устройство УППГ', '', '5_KS.jpg', '[a href=\"/docs/instrukciya_ppk_SNRI.tif\" target=_blank]Инструкция по эксплуатации ППК SNRI[/a];$$$[a href=\"/docs/instrukciya_ppk_Sempell.tif\" target=_blank]Инструкция по эксплуатации ППК Sempell[/a];$$$[a href=\"/docs/instrukciya_ppk_Dresser_Velvs.tif\" target=_blank]Инструкция по эксплуатации ППК Dresser Velves[/a];$$$', '[a href=\"/video/PPK.avi\" target=_blank]Предохранительный клапан[/a];$$$', '');
