INSERT INTO `ug_theory_copy_04_06_2015` VALUES ('1450', 'f0', 'Астраханский шазоперерабатывающий завод', '58,878,642,1099', 'Астраханский газоперерабатывающий завод (АГПЗ) предназначен для переработки пластового газа Астраханского газоконденсатного месторождения (АГКМ) с получением товарных продуктов: товарного газа, конденсата газового стабильного, серы технической газовой (жидкой, комовой, гранулированной марок «М» и «С»), неэтилированного бензина (Нормаль-80, Регуляр-92, Премиум Евро-95), топлива дизельного Евро, мазут марки 100 ГКТ, газов углеводородных сжиженных топливных (ПБТ, ПА), ШФЛУ и стабильного газового конденсата.', '', '0_main.jpg', '', '', '');
INSERT INTO `ug_theory_copy_04_06_2015` VALUES ('1452', 'f3', 'Горизонтальное факельное устройство', '1256,662,1372,727', 'Горизонтальное факельное устройство предназначено для сжигания пластовой смеси скважин одновременно с термической обезвоживанием жидких выбросов.', '', '3_VFU_GFU.jpg', '[a href=\"/docs/instrukciya_gfu.tif\" target=_blank]Инструкция по эксплуатации горизонтального факельного устройства[/a];$$$', '[a href=\"/video/GFU.avi\" target=_blank]Горизонтальное факельное устройство[/a];$$$', '');
