INSERT INTO `ug_theory_copy_31_08_2015` VALUES ('1450', 'f0', 'Астраханский шазоперерабатывающий завод', '58,878,642,1099', 'Астраханский газоперерабатывающий завод (АГПЗ) предназначен для переработки пластового газа Астраханского газоконденсатного месторождения (АГКМ) с получением товарных продуктов: товарного газа, конденсата газового стабильного, серы технической газовой (жидкой, комовой, гранулированной марок «М» и «С»), неэтилированного бензина (Нормаль-80, Регуляр-92, Премиум Евро-95), топлива дизельного Евро, мазут марки 100 ГКТ, газов углеводородных сжиженных топливных (ПБТ, ПА), ШФЛУ и стабильного газового конденсата.', '', 'f0_main.jpg', '', '', '');
