INSERT INTO `ug_theory_satm` VALUES ('420', 'f2', 'Манометр технический.', '238,95,286,138', 'Технический манометр, измеряющий давление в трубопроводе кислого газа до углового регулирующего клапан (высокая сторона).Предельно допустимое рабочее давление Р раб.- 40 МПа.Рекомендованный верхний предел измерительной шкалы манометра - 70 МПа (допускается 100 МПа).$$$', '', 'f2_Podogrevatel.jpg', '[a href=\"/docs/tablica_znacheniy_i_predelov_izmereniy.tif\" target=_blank]ТекстТаблица значений и пределов измерений[/a];$$$[a href=\"/docs/instrukciya_po_ekspluatacii_i_tehnicheskomu_obsluzhivanie_manometrov.tif\" target=_blank]Инструкция по эксплуатации и обслуживанию манометров[/a];$$$[a href=\"/docs/instrukciya_po_ekspluatacii_i_tehnicheskomu_obsluzhivanie_manometrov.tif\" target=_blank]Инструкция по эксплуатации и обслуживанию манометров[/a];$$$', '', '');
INSERT INTO `ug_theory_satm` VALUES ('422', 'f2', 'Манометр технический.', '238,95,286,138', 'Технический манометр, измеряющий давление в трубопроводе кислого газа до углового регулирующего клапан (высокая сторона).Предельно допустимое рабочее давление Р раб.- 40 МПа.Рекомендованный верхний предел измерительной шкалы манометра - 70 МПа (допускается 100 МПа).$$$', '', 'f2_Podogrevatel.jpg', '[a href=\"/docs/tablica_znacheniy_i_predelov_izmereniy.tif\" target=_blank]ТекстТаблица значений и пределов измерений[/a];$$$[a href=\"/docs/instrukciya_po_ekspluatacii_i_tehnicheskomu_obsluzhivanie_manometrov.tif\" target=_blank]Инструкция по эксплуатации и обслуживанию манометров[/a];$$$[a href=\"/docs/instrukciya_po_ekspluatacii_i_tehnicheskomu_obsluzhivanie_manometrov.tif\" target=_blank]Инструкция по эксплуатации и обслуживанию манометров[/a];$$$', '', '');
INSERT INTO `ug_theory_satm` VALUES ('424', 'f2', 'Манометр технический.', '617,624,662,664', 'Технический манометр, измеряющий давление в трубопроводе кислого газа после углового дроссельного клапана (низкая сторона).Предельно допустимое рабочее давление Р раб.- 13 МПа.Рекомендованный верхний предел измерительной шкалы манометра - 70 МПа (допускается 10 МПа).$$$', '', 'f2_Podogrevatel.jpg', '[a href=\"/docs/tablica_znacheniy_i_predelov_izmereniy.tif\" target=_blank]Таблица значений и пределов измерений[/a];$$$[a href=\"/docs/instrukciya_po_ekspluatacii_i_tehnicheskomu_obsluzhivanie_manometrov.tif\" target=_blank]Инструкция по эксплуатации и обслуживанию манометров[/a];$$$', '', '');
INSERT INTO `ug_theory_satm` VALUES ('426', 'f2', 'Манометр технический.', '821,364,862,404', 'Технический манометр, измеряющий давление в трубопроводе очищенного газа перед входом в устьевой подогреватель.$$$Предельно допустимое рабочее давление Р раб.- 5,6МПа.Рекомендованный верхний предел измерительной шкалы манометра - 10 МПа (допускается 16 МПа).$$$', '', 'f2_Podogrevatel.jpg', '[a href=\"/docs/tablica_znacheniy_i_predelov_izmereniy.tif\" target=_blank]Таблица значений и пределов измерений[/a];$$$[a href=\"/docs/instrukciya_po_ekspluatacii_i_tehnicheskomu_obsluzhivanie_manometrov.tif\" target=_blank]Инструкция по эксплуатации и обслуживанию манометров[/a];$$$', '', '');
INSERT INTO `ug_theory_satm` VALUES ('428', 'f2', 'Манометр технический.', '1349,458,1388,492', 'Технический манометр, измеряющий давление в трубопроводе очищенного газа (линия КИПиА) до блока осушки газа.$$$Предельно допустимое рабочее давление Р раб.- 3,2МПа.Рекомендованный верхний предел измерительной шкалы манометра - 6 МПа.$$$', '', 'f2_Podogrevatel.jpg', '[a href=\"/docs/tablica_znacheniy_i_predelov_izmereniy.tif\" target=_blank]Таблица значений и пределов измерений[/a];$$$[a href=\"/docs/instrukciya_po_ekspluatacii_i_tehnicheskomu_obsluzhivanie_manometrov.tif\" target=_blank]Инструкция по эксплуатации и обслуживанию манометров[/a];$$$', '', '');
