INSERT INTO `ug_theory_satm` VALUES ('890', 'f2', 'Датчик  давления', '462,623,501,658', 'Интеллектуальный датчик  давления, рабочий диапазон: от 0 до 16 МПа. При превышении давления выше 11,5 МПа происходит сигнализация в АСУ ТП.', '', 'f2_Podogrevatel.jpg', '', '', '');
INSERT INTO `ug_theory_satm` VALUES ('892', 'f2', 'Электроконтактный манометр', '302,791,396,838', 'Электроконтактный манометр, рабочий диапазон: от 0 до 160 МПа. При понижении давления ниже 6 МПа или его превышения выше 12 МПа происходит приостанов скважины. ', '', 'f2_Podogrevatel.jpg', '', '', '');
