INSERT INTO `ug_theory_satm` VALUES ('896', 'f2', 'Манометр электроконтактный', '1286,498,1369,532', 'Манометр электроконтактный, измеряющий давление топливного газа на горелки устьевого подогревателя скважины. Рабочий диапазон: от 0 до 1 МПа.  При превышении давления выше 1 МПа и понижении ниже 0,6 МПа происходит останов устьевого подогревателя скважины.', '', 'f2_Podogrevatel.jpg', '', '', '');
