INSERT INTO `ug_theory_satm` VALUES ('1396', 'f6', 'Дренажная ёмкость', '157,824,456,908', 'Дренажной емкость,которая связана с факельной системой и в которую по дренажным трубопроводам при предремонтном освобождении технологических трубопроводов сбрасывается накопившаяся в них жидкость. Жидкость из дренажной ёмкости вытесняется очищенным газом в факельный сепаратор, а оттуда насосами откачивается в газоконденсатопровод.', '', 'f6_FS_DE.jpg', '[a href=\"/docs/instrukciya_po_ekspluatacii_i_sosudov_rabotaushih_pod_davleniem.tif\" target=_blank]Инструкция по эксплуатации сосудов, работающих под давлением.[/a];$$$', '', '');
INSERT INTO `ug_theory_satm` VALUES ('1398', 'f6', 'Факельный сепаратор', '745,230,1118,354', 'Факельный сепаратор предназначен для приёма жидкости из дренажной ёмкости УППГ и перекачки в ГКП с помощью насосов, а также для отделения жидкости от газов при сбросе на факел УПП.', '', 'f6_FS_DE.jpg', '[a href=\"/docs/instrukciya_fakeljnyy_separator.tif\" target=_blank]Инструкция по эксплуатации факельного сепаратора[/a];$$$', '', '');
INSERT INTO `ug_theory_satm` VALUES ('1400', 'f6', 'Подпорный насос', '918,610,974,660', 'Подпорный (центробежный) насос факельного сепаратора. Предназначен для создания давления откачивающейся жидкости из факельного сепаратора на входе в дожимной насос. Производительность 5,2 м3/час, Рраб. = 0,12 МПа. Среда - конденсат, пластовая вода.', '', 'f6_FS_DE.jpg', '', '', '');
