INSERT INTO `ug_theory_satm` VALUES ('1406', 'f5', 'Предохранительный клапан', '156,382,238,440', 'Предохранительный клапан (ППК) предназначен для предохранения газоконденсатопровода возможного превышения рабочего давления. При превышении давления выше 11,2 МПа происходит сброс избыточного давления на вертикальное факельное устройство УППГ.', '', 'f5_KS.jpg', '[a href=\"/docs/instrukciya_ppk_Dresser_Velvs.tif\" target=_blank]Инструкция по эксплуатации ППК Dresser Velvs[/a];$$$[a href=\"/docs/instrukciya_ppk_Sempell.tif\" target=_blank]Инструкция по эксплуатации ППК Sempell[/a];$$$[a href=\"/docs/instrukciya_ppk_SNRI.tif\" target=_blank]Инструкция по эксплуатации ППК SNRI[/a];$$$', '[a href=\"/video/PPK.avi\" target=_blank]Предохранительный клапан[/a];$$$', '');
